  // );
  return (
    <div className="h-300 p-16 bg-blue-500 shadow-md flex items-center justify-center">
      <select id="point_of_departure" className="h-70 w-350 font-bold bg-white border border-gray-300 rounded-16 rounded-tr-none rounded-br-none p-2">
        <option value="">Откуда</option>
        <option value="option1">Опция 1</option>
        <option value="option2">Опция 2</option>
        <option value="option3">Опция 3</option>
      </select>
      <select id="point_of_arrival" className="h-70 w-350 font-bold bg-white border border-gray-300 rounded-16 rounded-tl-none rounded-bl-none p-2">
        <option value="">Куда</option>
        <option value="option1">Опция 1</option>
        <option value="option2">Опция 2</option>
        <option value="option3">Опция 3</option>
      </select>
      <select id="date_of_dispatch" className="h-70 w-350 font-bold bg-white border border-gray-300 rounded-16 rounded-tr-none rounded-br-none p-2">
        <option value="">Когда</option>
        <option value="option1">Опция 1</option>
        <option value="option2">Опция 2</option>
        <option value="option3">Опция 3</option>
      </select>
      <select id="date_of_departure" className="h-70 w-350 font-bold bg-white border border-gray-300 rounded-16 rounded-tl-none rounded-bl-none p-2">
        <option value="">Обратно</option>
        <option value="option1">Опция 1</option>
        <option value="option2">Опция 2</option>
        <option value="option3">Опция 3</option>
      </select>
      <button className='h-70 w-350 font-bold text-xl text-white bg-orange-500 rounded-16 p-2'>Найти рейс</button>
    </div>
  );
};

const styles = {
  pageContainer: {
    backgroundColor: '#e0f7fa',
  },
  flightsContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1rem',
    padding: '2rem',
  },
};

export default MainPage;
