import React, { ReactNode, useState, useEffect } from 'react';

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  children: ReactNode;
}

const SettingsModal: React.FC<ModalProps> = ({ isOpen, onClose, children }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    if (isOpen) {
      setIsVisible(true);
    } else {
      // Начинаем анимацию исчезновения, затем закрываем
      const timer = setTimeout(() => setIsVisible(false), 300); // 300 мс - время анимации
      return () => clearTimeout(timer);
    }
  }, [isOpen]);

  // Если окно закрыто и `isVisible` тоже `false`, ничего не отображаем
  if (!isOpen && !isVisible) return null;

  return (
    <div
      className={`fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 transition-opacity duration-300 ${
        isOpen ? 'opacity-100' : 'opacity-0'
      }`}
    >
      <div className="bg-white p-6 rounded-lg shadow-lg w-96 transition-transform duration-300 transform-gpu scale-95">
        <button onClick={onClose} className="text-gray-500 hover:text-gray-700 float-right">
          &times;
        </button>
        <div>{children}</div>
      </div>
    </div>
  );
};

export default SettingsModal;
